import os
import sys

class Plants:
    def __init__(self, temperatura, umidade, tipo_solo, luz):
        self.temperatura = temperatura
        self.umidade = umidade
        self.tipo_solo = tipo_solo
        self.luz = luz

    def gerar_recomendacoes(self, umidade_ideal, temperatura_ideal, solo_ideal, luz_ideal):
        recomendacoes = []

        if umidade_ideal < self.umidade:
            recomendacoes.append("A umidade tem que estar igual a 1")
        elif umidade_ideal > self.umidade:
            recomendacoes.append("Atenção a umidade, tem que estar igualmente a 1")
        elif umidade_ideal == 1 and self.umidade == 1:
            recomendacoes.append("Umidade ideal, uma vez na semana está ótimo.")

        if temperatura_ideal < self.temperatura:
            recomendacoes.append("Regar com mais frequência devido à temperatura alta")
        elif temperatura_ideal > self.temperatura:
            recomendacoes.append("Atenção! A temperatura está baixa")
        elif temperatura_ideal == 25 and self.temperatura == 25:
            recomendacoes.append("Temperatura adequada")

        if solo_ideal != self.tipo_solo:
            recomendacoes.append("Pode precisar de ajuste no solo")
        else:
            recomendacoes.append("Solo ideal")

        if luz_ideal == "meia-sombra" and self.luz == "meia-sombra":
            recomendacoes.append("A planta está na quantidade de luz adequada (meia-sombra)")
        else:
            recomendacoes.append(f"Ajuste a exposição à luz, ideal seria: {luz_ideal}")

        return recomendacoes

def coletar_entradas():
    temperatura = int(input("Digite a temperatura atual (°C): "))
    umidade = int(input("Quantas vezes por dia você rega a planta: "))
    tipo_solo = input("Digite o solo atual (terra, areia, argila): ")
    luz = input("Digite como é a exposição à luz (plena, meia-sombra, sombra): ")

    return temperatura, umidade, tipo_solo, luz

def main():
    temperatura, umidade, tipo_solo, luz = coletar_entradas()
    planta = Plants(temperatura, umidade, tipo_solo, luz)

    umidade_ideal = 1
    temperatura_ideal = 25
    solo_ideal = "terra"
    luz_ideal = "meia-sombra"

    recomendacoes = planta.gerar_recomendacoes(umidade_ideal, temperatura_ideal, solo_ideal, luz_ideal)

    print(f"\nRecomendações para a planta:")
    for recomendacao in recomendacoes:
        print(f"- {recomendacao}")

if __name__ == "__main__":
    main()
